package learn;

public class PrintAsciiValue
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
     char a='A';
     char b='2';
     char c=2;
     int d=a+b;
     int e=(char)(a+b);
     System.out.println(+a);
     System.out.println(+b);
     System.out.println(+c);
     System.out.println(+d);
     System.out.println(+e);
		
	}

}
